mapper.select:
select ii.ii_id as admissionId, ii.p_id as pid, ii.pt_name as ptName , p.p_birth as birth, ii.ii_ad_number as admissionNumber, lr.leave_record_id as id,
ii.age as age, ii.dept_name as deptName,p.p_name as name, iw.iward_id as wardId, b.b_id as bid,
p.p_sex as sex, ii.ii_date_admission as dateOfAdmission, lr.leave_date as leaveDate, lr.admission as admission,
admission_diagnosis as admissionDiagnosis, diagnosis_process as diagnosisProcess, discharge_diagnosis as dischargeDiagnosis,
discharge_advice as dischargeAdvice, doctor as doctor, lr.discharge_status as dischargeStatus, ii.ii_status as inpatientStatus
from inpatient_information ii,inpatient_ward iw,bed b,patient p,leave_record lr
where ii.p_id = p.p_id and ii.ward_id = iw.wid and ii.bed_id = b.id and lr.admission_id = ii.ii_id

    and lr.doctor = #{condition.doctor}



    and ii.p_id like #{pid}



    and p.p_name like #{pname}



    and ii.ii_ad_number like #{admissionNumber}


    and ii.ii_status = #{condition.status}


    and lr.leave_date between #{condition.leaveDateStart} and #{condition.leaveDateEnd}

select ii.ii_id as admissionId, ii.p_id as pid , ii.pt_name as ptName , p.p_birth as birth, ii.ii_ad_number as admissionNumber, lr.leave_record_id as id,
ii.age as age, ii.dept_name as deptName,p.p_name as name, iw.iward_id as wardId, b.b_id as bid,
p.p_sex as sex, ii.ii_date_admission as dateOfAdmission, lr.leave_date as leaveDate, lr.admission as admission,
admission_diagnosis as admissionDiagnosis, diagnosis_process as diagnosisProcess, discharge_diagnosis as dischargeDiagnosis,
discharge_advice as dischargeAdvice, doctor as doctor, lr.discharge_status as dischargeStatus , ii.ii_status as inpatientStatus
from inpatient_information ii,inpatient_ward iw,bed b,patient p,leave_record lr
where  ii.p_id = p.p_id and ii.ward_id = iw.wid and ii.bed_id = b.id and lr.admission_id = ii.ii_id

    and lr.doctor = #{condition.doctor}



    and ii.p_id like #{pid}



    and p.p_name like #{pname}



    and ii.ii_ad_number like #{admissionNumber}


    and ii.ii_status = #{condition.status}


    and lr.leave_date between #{condition.leaveDateStart} and #{condition.leaveDateEnd}

select count(1)
from leave_record lr
where  lr.admission_id = #{admissionId}
